mapper.select:
  SELECT c.usernick,a.pointspk,a.pointsid,a.userid,a.taskid,a.pointstype,a.pointsupdate,a.points,a.pointreason,DATE_FORMAT(a.createtime,'%Y-%m-%d %H:%i:%s') AS createtime,a.createuser,a.updatetime,a.updateuser,b.dicinfoname AS pointstypename  
  FROM t_points_info a 
  LEFT JOIN t_data_dic_info b ON a.pointstype=b.dicinfoid
  LEFT JOIN t_user_info c on a.createuser = c.userid


			and a.createtime >= #{datefrom}


			and a.createtime <= #{dateto}


	    	and c.usernick = #{usernick}


	limit #{page},#{rows}

   SELECT count(1) FROM t_points_info a 
  LEFT JOIN t_data_dic_info b ON a.pointstype=b.dicinfoid
  LEFT JOIN t_user_info c on a.createuser = c.userid


			and a.createtime >= #{datefrom}


			and a.createtime <= #{dateto}


	    	and c.usernick = #{usernick}

  SELECT c.usernick,a.pointspk,a.pointsid,a.userid,a.taskid,a.pointstype,a.pointsupdate,a.points,a.pointreason,DATE_FORMAT(a.createtime,'%Y-%m-%d %H:%i:%s') AS createtime,a.createuser,a.updatetime,a.updateuser,b.dicinfoname as pointstypename FROM t_points_info a
	LEFT JOIN t_data_dic_info b ON a.pointstype=b.dicinfoid
	INNER JOIN (SELECT a.* FROM t_user_info a WHERE a.agentid IN (SELECT agentid FROM t_agent_info WHERE agentuserid=#{userid})) c ON a.createuser=c.userid


			and a.createtime >= #{datefrom}


			and a.createtime <= #{dateto}


			and a.createuser = #{createuser}


			and c.usernick = #{usernick}


	limit #{page},#{rows}

    SELECT count(1) FROM t_points_info a
	LEFT JOIN t_data_dic_info b ON a.pointstype=b.dicinfoid
	INNER JOIN (SELECT a.* FROM t_user_info a WHERE a.agentid IN (SELECT agentid FROM t_agent_info WHERE agentuserid=#{userid})) c ON a.createuser=c.userid


			and a.createtime >= #{datefrom}


			and a.createtime <= #{dateto}


			and a.createuser = #{createuser}

mapper.insert:
