mapper.select:
  	SELECT a.taskid,COUNT(1) AS counts FROM t_task_info a
	LEFT JOIN t_task_detailinfo b ON a.taskid= b.taskid


			and a.tasktype=#{tasktype}

		and a.taskstate='16' AND DATE_FORMAT(a.taskdate,'%Y%m%d') = DATE_FORMAT(NOW(),'%Y%m%d') AND b.taskstate='22' GROUP BY a.taskid HAVING counts >= #{counts}

SELECT g.usernick,DATE_FORMAT(a.taskdate,'%Y-%m-%d') AS taskdaterange,a.taskpk,a.taskid, a.tasktype, a.taskkeynum, a.tasktitle, a.taskwirelesstitle, a.taskstartdate,a.taskenddate,a.taskdate, a.taskreleasekeyword,a.taskkeyword,a.taskhourcounts, a.taskminprice,a.taskmaxprice,a.tasksearchType, a.taskimgztc,a.taskimgpt,a.flowcount, a.Collectioncount,a.Shoppingcount,a.storecollectioncount,a.fakechatcount,a.tasktmallapppct,a.creativetitle, a.taskgprspct,a.taskPlus,a.taskduration, a.subtractpoints,a.taskstate,DATE_FORMAT(a.createtime,'%Y-%m-%d %H:%i:%s') AS createtime, a.createuser,a.updatetime,a.updateuser,b.dicinfoname, CASE WHEN a.tasktype='34' THEN IFNULL(c.finishcollectioncount,0) WHEN a.tasktype='33' AND IFNULL(f.finishcount,0)>=IFNULL(c.finishcollectioncount,0) AND IFNULL(f.finishcount,0)>=IFNULL(c.finishshoppingcount,0) THEN IFNULL(f.finishcount,0) WHEN a.tasktype='33' AND IFNULL(f.finishcount,0)>=IFNULL(c.finishcollectioncount,0) AND IFNULL(f.finishcount,0) < IFNULL(c.finishshoppingcount,0) THEN IFNULL(c.finishshoppingcount,0) WHEN a.tasktype='33' AND IFNULL(f.finishcount,0) < IFNULL(c.finishcollectioncount,0) AND IFNULL(f.finishcount,0)>=IFNULL(c.finishshoppingcount,0) THEN IFNULL(c.finishcollectioncount,0) WHEN a.tasktype='33' AND IFNULL(f.finishcount,0) < IFNULL(c.finishcollectioncount,0) AND IFNULL(f.finishcount,0) < IFNULL(c.finishshoppingcount,0) AND IFNULL(c.finishcollectioncount,0) < IFNULL(c.finishshoppingcount,0) THEN IFNULL(c.finishshoppingcount,0) ELSE IFNULL(c.finishcollectioncount,0) END finishflowcount, IFNULL(c.finishcollectioncount,0) AS finishcollectioncount,IFNULL(c.finishshoppingcount,0) AS finishshoppingcount,IFNULL(c.finishstorecollectioncount,0) AS finishstorecollectioncount,IFNULL(c.finishfakechatcount,0) AS finishfakechatcount,IFNULL(d.errorcount,0) AS errorcount,e.dicinfoname AS tasktypename FROM t_task_info a LEFT JOIN (SELECT dicinfoid,dicinfoname FROM t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid LEFT JOIN t_data_dic_info e ON a.tasktype=e.dicinfoid LEFT JOIN t_task_detailinfo_flow f ON a.taskid = f.taskid LEFT JOIN (SELECT taskid,SUM(IFNULL(iscollection,0)) AS finishcollectioncount,SUM(IFNULL(isshopping,0)) AS finishshoppingcount,SUM(IFNULL(isstorecollection,0)) AS finishstorecollectioncount,SUM(IFNULL(isfakechat,0)) AS finishfakechatcount FROM t_task_detailinfo WHERE taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid LEFT JOIN (SELECT aa.taskid,SUM(IFNULL(aa.isshoppingll,0))+SUM(IFNULL(aa.iscollectionll,0))+SUM(IFNULL(aa.ztc,0))+SUM(IFNULL(aa.isshoppingztc,0)) AS errorcount FROM( SELECT taskid,tasktype,CASE WHEN isshopping=1 AND tasktype='33' THEN 1 ELSE 0 END AS isshoppingll, CASE WHEN iscollection=1 AND tasktype='33' THEN 1 ELSE 0 END AS iscollectionll,CASE WHEN isshopping=0 AND iscollection=0 AND tasktype='34' THEN 1 ELSE 0 END AS ztc, CASE WHEN isshopping=1 AND iscollection=1 AND tasktype='34' THEN 1 ELSE 0 END AS isshoppingztc FROM t_task_detailinfo WHERE taskstate='22') aa GROUP BY aa.taskid) d ON a.taskid=d.taskid LEFT JOIN t_user_info g ON a.createuser = g.userid
and a.taskdate+0 >= #{today}+0
and a.taskdate+0 < #{before}+0
and a.taskid like '%${taskid}%'
and a.taskpk = #{taskpk}
and a.createtime >= #{datefrom}
and a.createtime <= #{dateto}
and a.createuser = #{userid}
and a.taskkeyword like '%${keyword}%'
and a.taskkeyword like '%${taskkeyword}%'
and a.tasktype = #{tasktype}
and a.taskstate = #{taskstate}
and a.taskkeynum = #{taskkeynum}
and a.taskid = #{taskid}
ORDER BY a.taskpk+0 DESC LIMIT #{page},#{rows}

SELECT g.usernick,DATE_FORMAT(a.taskdate,'%Y-%m-%d') AS taskdaterange,a.taskpk,a.taskid, a.tasktype, a.taskkeynum, a.tasktitle, a.taskwirelesstitle, a.taskstartdate,a.taskenddate,a.taskdate, a.taskreleasekeyword,a.taskkeyword,a.taskhourcounts, a.taskminprice,a.taskmaxprice,a.tasksearchType,a.creativetitle, a.taskimgztc,a.taskimgpt,a.flowcount, a.Collectioncount,a.Shoppingcount,a.storecollectioncount,a.fakechatcount,a.tasktmallapppct, a.taskgprspct,a.taskPlus,a.taskduration, a.subtractpoints,a.taskstate,DATE_FORMAT(a.createtime,'%Y-%m-%d %H:%i:%s') AS createtime, a.createuser,a.updatetime,a.updateuser,b.dicinfoname, CASE WHEN a.tasktype='34' THEN IFNULL(c.finishcollectioncount,0) WHEN a.tasktype='33' AND IFNULL(h.finishcount,0)>=IFNULL(c.finishcollectioncount,0) and IFNULL(h.finishcount,0)>=IFNULL(c.finishshoppingcount,0) THEN IFNULL(h.finishcount,0) WHEN a.tasktype='33' AND IFNULL(h.finishcount,0)>=IFNULL(c.finishcollectioncount,0) and IFNULL(h.finishcount,0) < IFNULL(c.finishshoppingcount,0) THEN IFNULL(c.finishshoppingcount,0) WHEN a.tasktype='33' AND IFNULL(h.finishcount,0) < IFNULL(c.finishcollectioncount,0) and IFNULL(h.finishcount,0)>=IFNULL(c.finishshoppingcount,0) THEN IFNULL(c.finishcollectioncount,0) WHEN a.tasktype='33' AND IFNULL(h.finishcount,0) < IFNULL(c.finishcollectioncount,0) and IFNULL(h.finishcount,0) < IFNULL(c.finishshoppingcount,0) and IFNULL(c.finishcollectioncount,0) < IFNULL(c.finishshoppingcount,0) THEN IFNULL(c.finishshoppingcount,0) ELSE IFNULL(c.finishcollectioncount,0) END finishflowcount, IFNULL(c.finishcollectioncount,0) AS finishcollectioncount,IFNULL(c.finishshoppingcount,0) AS finishshoppingcount,IFNULL(c.finishstorecollectioncount,0) AS finishstorecollectioncount,IFNULL(c.finishfakechatcount,0) AS finishfakechatcount,IFNULL(d.errorcount,0) AS errorcount,e.dicinfoname AS tasktypename FROM t_task_info a LEFT JOIN (SELECT dicinfoid,dicinfoname FROM t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid LEFT JOIN t_data_dic_info e ON a.tasktype=e.dicinfoid LEFT JOIN t_task_detailinfo_flow h ON a.taskid = h.taskid LEFT JOIN (SELECT taskid,SUM(iscollection) AS finishcollectioncount,SUM(isshopping) AS finishshoppingcount,SUM(isstorecollection) AS finishstorecollectioncount,SUM(isfakechat) AS finishfakechatcount FROM t_task_detailinfo WHERE taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid LEFT JOIN (SELECT aa.taskid,SUM(IFNULL(aa.isshoppingll,0))+SUM(IFNULL(aa.iscollectionll,0))+SUM(IFNULL(aa.ztc,0))+SUM(IFNULL(aa.isshoppingztc,0)) AS errorcount FROM( SELECT taskid,tasktype,CASE WHEN isshopping=1 AND tasktype='33' THEN 1 ELSE 0 END AS isshoppingll, CASE WHEN iscollection=1 AND tasktype='33' THEN 1 ELSE 0 END AS iscollectionll,CASE WHEN isshopping=0 AND iscollection=0 AND tasktype='34' THEN 1 ELSE 0 END AS ztc, CASE WHEN isshopping=1 AND iscollection=1 AND tasktype='34' THEN 1 ELSE 0 END AS isshoppingztc FROM t_task_detailinfo WHERE taskstate='22') aa GROUP BY aa.taskid) d ON a.taskid=d.taskid LEFT JOIN t_user_info g ON a.createuser = g.userid INNER JOIN (SELECT a.* FROM t_user_info a WHERE a.agentid IN (SELECT agentid FROM t_agent_info WHERE agentuserid=#{userid})) f ON a.createuser=f.userid
and a.taskid like '%${taskid}%'
and a.taskpk = #{taskpk}
and a.taskdate+0 >= #{today}+0
and a.taskdate+0 < #{before}+0
and a.createtime >= #{datefrom}
and a.createtime <= #{dateto}
and a.taskkeyword like '%${taskkeyword}%'
and a.taskkeynum = #{taskkeynum}
and a.tasktype = #{tasktype}
and a.createuser = #{createuser}
ORDER BY a.taskpk+0 DESC LIMIT #{page},#{rows}

   SELECT count(1) FROM t_task_info a
     LEFT JOIN (SELECT dicinfoid,dicinfoname FROM t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid
	LEFT JOIN t_data_dic_info e ON a.tasktype=e.dicinfoid
	LEFT JOIN t_task_detailinfo_flow h ON a.taskid = h.taskid
	LEFT JOIN (SELECT taskid,SUM(iscollection) AS finishcollectioncount,SUM(isshopping) AS finishshoppingcount,SUM(isstorecollection) AS finishstorecollectioncount,SUM(isfakechat) AS finishfakechatcount FROM t_task_detailinfo WHERE taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid
	LEFT JOIN (SELECT taskid,(SUM(iscollection)+SUM(isshopping)+SUM(isstorecollection)+SUM(isfakechat)) AS errorcount FROM t_task_detailinfo WHERE taskstate='22' GROUP BY taskid) d ON a.taskid=d.taskid
	LEFT JOIN t_user_info g ON a.createuser = g.userid
	INNER JOIN (SELECT a.* FROM t_user_info a WHERE a.agentid IN (SELECT agentid FROM t_agent_info WHERE agentuserid=#{userid})) f ON a.createuser=f.userid


    		and a.taskid like '%${taskid}%'


    		and a.taskpk = #{taskpk}


			and a.taskdate+0 >= #{today}+0


			and a.taskdate+0 < #{before}+0


    		and a.createtime >= #{datefrom}


    		and a.createtime <= #{dateto}


    		and a.taskkeyword like '%${taskkeyword}%'


    		and a.taskkeynum = #{taskkeynum}


    		and a.tasktype = #{tasktype}


    		and a.createuser = #{createuser}

    SELECT count(1) FROM t_task_info a
	LEFT JOIN (SELECT dicinfoid,dicinfoname FROM t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid
	LEFT JOIN t_data_dic_info e ON a.tasktype=e.dicinfoid
	LEFT JOIN t_task_detailinfo_flow f ON a.taskid = f.taskid
	LEFT JOIN (SELECT taskid,SUM(iscollection) AS finishcollectioncount,SUM(isshopping) AS finishshoppingcount,SUM(isstorecollection) AS finishstorecollectioncount,SUM(isfakechat) AS finishfakechatcount FROM t_task_detailinfo WHERE taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid
	LEFT JOIN (SELECT taskid,(SUM(iscollection)+SUM(isshopping)+SUM(isstorecollection)+SUM(isfakechat)) AS errorcount FROM t_task_detailinfo WHERE taskstate='22' GROUP BY taskid) d ON a.taskid=d.taskid
	LEFT JOIN t_user_info g ON a.createuser = g.userid


			and a.taskdate+0 >= #{today}+0


			and a.taskdate+0 < #{before}+0


    		and a.taskid like '%${taskid}%'


    		and a.taskpk = #{taskpk}


    		and a.createtime >= #{datefrom}


    		and a.createtime <= #{dateto}


    		and a.createuser = #{userid}


    		and a.taskkeyword like '%${keyword}%'


    		and a.taskkeyword like '%${taskkeyword}%'


    		and a.tasktype = #{tasktype}


    		and a.taskstate = #{taskstate}


    		and a.taskkeynum = #{taskkeynum}


    		and a.taskid = #{taskid}

select * from t_task_info


		AND taskstate=#{taskstate} 


		AND taskdate = #{taskdate} 


		AND taskkeynum = #{taskkeynum} 


order by taskpk+0 desc

mapper.update:
update t_task_info set taskstate=#{taskstate},updatetime=#{updatetime},updateuser=#{updateuser} 


		and createuser=#{userid}	


		and taskid=#{taskid}
