      #{tRechargeInfoCustom.rechargemoney,jdbcType=INTEGER}, #{tRechargeInfoCustom.rechargestate,jdbcType=VARCHAR}, #{tRechargeInfoCustom.createtime,jdbcType=VARCHAR}, 
      #{tRechargeInfoCustom.createuser,jdbcType=VARCHAR}, #{tRechargeInfoCustom.updatetime,jdbcType=VARCHAR}, #{tRechargeInfoCustom.updateuser,jdbcType=VARCHAR}
      )

mapper.select:
	select * from t_recharge_info 


    		and createuser >= #{createuser}


    		and createtime >= #{datefrom}


    		and createtime <= #{dateto}


	order by createtime desc LIMIT #{page},#{rows}

	select count(1) from t_recharge_info 


    		and createuser >= #{createuser}


    		and createtime >= #{datefrom}


    		and createtime <= #{dateto}

	select * from t_recharge_info 


    		and createuser >= #{createuser}


    		and createtime >= #{datefrom}


    		and createtime <= #{dateto}


	order by createtime,rechargepk desc
