mapper.select:
    SELECT a.taskpk,a.taskid, a.tasktype, 
     a.taskkeynum, a.tasktitle, a.taskwirelesstitle, 
     a.taskstartdate,a.taskenddate,a.taskdate, 
     a.taskreleasekeyword,a.taskkeyword,a.taskhourcounts, 
     a.taskminprice,a.taskmaxprice,a.tasksearchType, 
     a.taskimgztc,a.taskimgpt,a.flowcount, 
     a.Collectioncount,a.Shoppingcount,a.tasktmallapppct, 
     a.taskgprspct,a.taskPlus,a.taskduration, 
     a.subtractpoints,a.taskstate,DATE_FORMAT(a.createtime,'%Y-%m-%d %H:%i:%s') AS createtime, 
     a.createuser,a.updatetime,a.updateuser,b.dicinfoname,IFNULL(c.finishflowcount,0) AS finishflowcount,IFNULL(c.finishcollectioncount,0) AS finishcollectioncount,IFNULL(c.finishshoppingcount,0) AS finishshoppingcount,IFNULL(d.errorcount,0) AS errorcount,e.dicinfoname AS tasktypename FROM t_task_info a
	LEFT JOIN (select dicinfoid,dicinfoname from t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid
	left join t_data_dic_info e ON a.tasktype=e.dicinfoid
	LEFT JOIN (SELECT taskid,COUNT(isflow) AS finishflowcount,COUNT(iscreativetitle) AS finishcollectioncount,COUNT(isshopcollect) AS finishshoppingcount FROM t_task_detailinfo where taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid
	LEFT JOIN (SELECT taskid,COUNT(isflow) AS errorcount FROM t_task_detailinfo WHERE taskstate='22' GROUP BY taskid) d ON a.taskid=d.taskid


    		and a.taskid like '%${taskid}%'


    		and a.taskpk = #{taskpk}


    		and a.createtime >= #{datefrom}


    		and a.createtime <= #{dateto}


    		and a.createuser = #{userid}


    		and a.taskkeyword like '%${keyword}%'


    		and a.tasktype = #{tasktype}


    		and a.taskstate = #{state}


    		and a.taskkeynum = #{taskkeynum}


    		and a.taskid = #{taskid}


     ORDER BY a.taskpk DESC LIMIT #{page},#{rows}

    SELECT f.usernick,a.taskpk,a.taskid,a.tasktype,a.taskkeynum,a.tasktitle,a.taskwirelesstitle,a.taskstartdate,a.taskenddate,a.taskdate,a.taskreleasekeyword,a.taskkeyword,a.taskhourcounts,
     a.taskminprice,a.taskmaxprice,a.tasksearchType,a.taskimgztc,a.taskimgpt,a.flowcount,a.Collectioncount,a.Shoppingcount,a.tasktmallapppct,a.taskgprspct,a.taskPlus,a.taskduration, 
     a.subtractpoints,a.taskstate,DATE_FORMAT(a.createtime,'%Y-%m-%d %H:%i:%s') AS createtime,a.updatetime,a.updateuser,b.dicinfoname,IFNULL(c.finishflowcount,0) AS finishflowcount,IFNULL(c.finishcollectioncount,0) AS finishcollectioncount,IFNULL(c.finishshoppingcount,0) AS finishshoppingcount,IFNULL(d.errorcount,0) AS errorcount,e.dicinfoname AS tasktypename FROM t_task_info a
	LEFT JOIN (SELECT dicinfoid,dicinfoname FROM t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid
	LEFT JOIN t_data_dic_info e ON a.tasktype=e.dicinfoid
	LEFT JOIN (SELECT taskid,COUNT(isflow) AS finishflowcount,COUNT(iscreativetitle) AS finishcollectioncount,COUNT(isshopcollect) AS finishshoppingcount FROM t_task_detailinfo WHERE taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid
	LEFT JOIN (SELECT taskid,COUNT(isflow) AS errorcount FROM t_task_detailinfo WHERE taskstate='22' GROUP BY taskid) d ON a.taskid=d.taskid
	INNER JOIN (SELECT a.* FROM t_user_info a WHERE a.agentid IN (SELECT agentid FROM t_agent_info WHERE agentuserid='1000024')) f ON a.createuser=f.userid

    		and a.taskid like '%${taskid}%'


    		and a.taskpk = #{taskpk}


    		and a.createtime >= #{datefrom}


    		and a.createtime <= #{dateto}


    		and a.taskkeyword like '%${taskkeyword}%'


    		and a.taskkeynum = #{taskkeynum}


    		and a.tasktype = #{tasktype}


    		and a.createuser = #{createuser}


     ORDER BY a.taskpk DESC LIMIT #{page},#{rows}

    SELECT count(1) FROM t_task_info a
	LEFT JOIN (select dicinfoid,dicinfoname from t_data_dic_info WHERE dictypecode='runstate') b ON a.taskstate=b.dicinfoid
	left join t_data_dic_info e ON a.tasktype=e.dicinfoid
	LEFT JOIN (SELECT taskid,COUNT(isflow) AS finishflowcount,COUNT(iscreativetitle) AS finishcollectioncount,COUNT(isshopcollect) AS finishshoppingcount FROM t_task_detailinfo where taskstate='21' GROUP BY taskid) c ON a.taskid=c.taskid
	LEFT JOIN (SELECT taskid,COUNT(isflow) AS errorcount FROM t_task_detailinfo WHERE taskstate='22' GROUP BY taskid) d ON a.taskid=d.taskid
	INNER JOIN (SELECT a.* FROM t_user_info a WHERE a.agentid IN (SELECT agentid FROM t_agent_info WHERE agentuserid=#{userid})) f ON a.createuser=f.userid


    		and a.taskid like '%${taskid}%'


    		and a.taskpk = #{taskpk}


    		and a.createtime >= #{datefrom}


    		and a.createtime <= #{dateto}


    		and a.taskkeyword like '%${taskkeyword}%'


    		and a.taskkeynum = #{taskkeynum}


    		and a.tasktype = #{tasktype}


    		and a.createuser = #{createuser}

select count(1) from t_task_info


